package execution

import (
	"github.com/dapperlabs/flow-go/crypto"
	"github.com/dapperlabs/flow-go/model/flow"
)

// TransactionInCollection represents a transaction inside a collection
type TransactionInCollection struct {
	TxHash              crypto.Hash
	CollectionHash      crypto.Hash
	TxIndexInCollection uint64
}

// ExecutedTransaction are generated by computer and pass to chunker
type ExecutedTransaction struct {
	Tx         *flow.Transaction
	GasSpent   uint64
	MaxGas     uint64
	StartState StateCommitment
	EndState   StateCommitment
}
